import{_ as e,c as t,o as a,e as i}from"./404.md.f0d453c2.js";const m=JSON.parse('{"title":"Advanced Manifest","description":"How to work with manifests - a more detailed guide [UNDER CONSTRUCTION]","frontmatter":{"title":"Advanced Manifest","category":"Extra","description":"How to work with manifests - a more detailed guide [UNDER CONSTRUCTION]","nav_order":4,"prefix":"d.","mentions":["MRBBATES1","Luthorius","SirLich","smell-of-curry","MedicalJewel105","QuazChick"]},"headers":[{"level":2,"title":"UUIDs","slug":"uuids","link":"#uuids","children":[{"level":3,"title":"How to Generate the correct UUID","slug":"how-to-generate-the-correct-uuid","link":"#how-to-generate-the-correct-uuid","children":[]}]},{"level":2,"title":"","slug":"","link":"#","children":[{"level":3,"title":"UUID FAQ","slug":"uuid-faq","link":"#uuid-faq","children":[]}]}],"relativePath":"guide/advancedmanifest.md"}'),r={name:"guide/advancedmanifest.md"},n=i('<div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This is an appendix page. You can start the guide from the beginning <a href="/guide/index.html">here</a>.</p></div><p>This page is desgined to go into more detail about the manifest.json file, here we will cover what UUIDs are in more detail and how to add them. We will explain the use of dependencies, the different format versions, and how to include meta-data.</p><p>We will also go over the version differences between Behaviour packs, Resource packs, and Skin packs.</p><h2 id="uuids" tabindex="-1">UUIDs <a class="header-anchor" href="#uuids" aria-hidden="true">#</a></h2><p>UUID is an abbreviation for Universal Unique Identifier, there are 5 UUID versions plus one common unofficial version, A UUID is a 36 character string containing numbers, letters, and dashes.</p><p>Minecraft uses Version 4: Variant 1, which is completely random. This is what creates your pack&#39;s unique identity in Minecraft.</p><h3 id="how-to-generate-the-correct-uuid" tabindex="-1">How to Generate the correct UUID <a class="header-anchor" href="#how-to-generate-the-correct-uuid" aria-hidden="true">#</a></h3><p>You can use online sites such as <a href="https://www.uuidgenerator.net/version4/" target="_blank" rel="noreferrer">UUID Generator</a> and <a href="https://www.uuidtools.com/generate/v4" target="_blank" rel="noreferrer">UUID Tools</a> to generate the correct version required for Minecraft.</p><h2 id="" tabindex="-1"><a class="header-anchor" href="#" aria-hidden="true">#</a></h2><h3 id="uuid-faq" tabindex="-1">UUID FAQ <a class="header-anchor" href="#uuid-faq" aria-hidden="true">#</a></h3><ul><li><p><strong>Are UUIDs Case-sensitive?</strong></p><ul><li><em>No, UUIDs are written in base 16 which uses numbers 0-9 and characters a-f. There is no distinction between upper and lowercase letters.</em></li></ul></li><li><p><strong>Can I use the same UUID for the header and the modules UUID?</strong></p><ul><li><em>No, the UUID for the header and the module needs to be different.</em></li></ul></li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>This page is under construction!</p></div>',12),s=[n];function o(d,c,l,h,u,p){return a(),t("div",null,s)}const U=e(r,[["render",o]]);export{m as __pageData,U as default};
